import React from "react";
import styled from "styled-components";

// import './Button.css'; --> Not req. as I'll be using modular css.
// const Button = props => {
//   return (
//     <button type={props.type} className="button" onClick={props.onClick}>
//       {props.children}
//     </button>
//   );
// };

const Button = (props) => {

  // below will return a styled button (React Component)
  const StyledButton = styled.button`
    font: inherit;
    padding: 0.5rem 1.5rem;
    border: 1px solid #8b005d;
    color: white;
    background: #8b005d;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.26);
    cursor: pointer;
    &:focus {
      outline: none;
    }
    &:hover,
    &:active {
      background: #ac0e77;
      border-color: #ac0e77;
      box-shadow: 0 0 8px rgba(0, 0, 0, 0.26);
    }
  `;
  return (
    <div>
      <StyledButton
        type={props.type}
        className="button"
        onClick={props.onClick}
      >
        {props.children}
      </StyledButton>
    </div>
  );
};

export default Button;
