import React from "react";
import styled from "styled-components";

// import './Button.css'; --> Not req. as I'll be using modular css.
// const Button = props => {
//   return (
//     <button type={props.type} className="button" onClick={props.onClick}>
//       {props.children}
//     </button>
//   );
// };

const Button = (props) => {
  const styledButton = styled.button`

  `;
  return (
    <div>
      <styledButton
        type={props.type}
        className="button"
        onClick={props.onClick}
      >
        {props.children}
      </styledButton>
    </div>
  );
};

export default Button;
